CREATE TABLE DEADLINE (id NUMBER NOT NULL, COMPLETION NUMBER, DEADLINE_DATE TIMESTAMP, ESCALATION_NAME VARCHAR2(255), TASK_ID NUMBER, PRIMARY KEY (id));
CREATE TABLE GENERIC_HUMAN_ROLE (GHR_ID NUMBER NOT NULL, type VARCHAR2(23), TASK_ID NUMBER, PRIMARY KEY (GHR_ID));
CREATE TABLE HUMANROLE_ORGENTITY (HUMANROLE_ID NUMBER, ORGENTITY_ID NUMBER);
CREATE TABLE MESSAGE (MESSAGE_ID NUMBER NOT NULL, NAME VARCHAR2(255), TYPE VARCHAR2(255) NOT NULL, TASK_ID NUMBER, PRIMARY KEY (MESSAGE_ID));
CREATE TABLE MESSAGE_PART (MESSAGE_PART_ID NUMBER NOT NULL, MESSAGE_PART_CONTENT CLOB, MESSAGE_PART_NAME VARCHAR2(255), MESSAGE_PART_TYPE VARCHAR2(255), MESSAGE_MESSAGE_ID NUMBER, KEY0 VARCHAR2(255), PRIMARY KEY (MESSAGE_PART_ID));
CREATE TABLE OPENJPA_SEQUENCE_TABLE (ID NUMBER NOT NULL, SEQUENCE_VALUE NUMBER, PRIMARY KEY (ID));
CREATE TABLE ORG_ENTITY (ORG_ENTITY_ID NUMBER NOT NULL, ORG_ENTITY_NAME VARCHAR2(255), ORG_ENTITY_TYPE VARCHAR2(255), PRIMARY KEY (ORG_ENTITY_ID));
CREATE TABLE PRESENTATION_ELEMENT (id NUMBER NOT NULL, CONTENT VARCHAR2(255), XML_LANG VARCHAR2(255), PE_TYPE VARCHAR2(31), CONTENT_TYPE VARCHAR2(255), TASK_ID NUMBER, PRIMARY KEY (id));
CREATE TABLE PRESENTATION_PARAM (id NUMBER NOT NULL, NAME VARCHAR2(255), type VARCHAR2(20), VALUE VARCHAR2(255), TASK_ID NUMBER, PRIMARY KEY (id));
CREATE TABLE TASK (id NUMBER NOT NULL, ACTIVATION_TIME TIMESTAMP, CREATED_ON TIMESTAMP, ESCALATED NUMBER, EXPIRATION_TIME TIMESTAMP, NAME VARCHAR2(255) NOT NULL, PRIORITY NUMBER, SKIPABLE NUMBER, STATUS VARCHAR2(255) NOT NULL, STATUS_BEFORE_SUSPENSION VARCHAR2(255), TYPE VARCHAR2(255) NOT NULL, PRIMARY KEY (id));
CREATE TABLE TASK_ATTACHMENT (id NUMBER NOT NULL, ACCESS_TYPE VARCHAR2(255), ATTACHED_AT TIMESTAMP, CONTENT_TYPE VARCHAR2(255), ATTACHMENT_NAME VARCHAR2(255), VALUE VARCHAR2(255), ATTACHED_BY NUMBER, TASK_ID NUMBER, PRIMARY KEY (id));
CREATE TABLE TASK_COMMENT (id NUMBER NOT NULL, COMMENT_TEXT VARCHAR2(8192), COMMENTED_ON TIMESTAMP, TASK_ID NUMBER, COMMENTEDBY_ORG_ENTITY_ID NUMBER, PRIMARY KEY (id));
CREATE INDEX I_DEDLINE_TASK ON DEADLINE (TASK_ID);
CREATE INDEX I_GNRC_RL_TASK ON GENERIC_HUMAN_ROLE (TASK_ID);
CREATE INDEX I_HMNRTTY_ELEMENT ON HUMANROLE_ORGENTITY (ORGENTITY_ID);
CREATE INDEX I_HMNRTTY_HUMANROLE_ID ON HUMANROLE_ORGENTITY (HUMANROLE_ID);
CREATE INDEX I_MESSAGE_TASK ON MESSAGE (TASK_ID);
CREATE INDEX I_MSSGPRT_MESSAGE ON MESSAGE_PART (MESSAGE_MESSAGE_ID);
CREATE INDEX I_PRSNMNT_DTYPE ON PRESENTATION_ELEMENT (PE_TYPE);
CREATE INDEX I_PRSNMNT_TASK ON PRESENTATION_ELEMENT (TASK_ID);
CREATE INDEX I_PRSNPRM_TASK ON PRESENTATION_PARAM (TASK_ID);
CREATE INDEX I_TSK_MNT_ATTACHEDBY ON TASK_ATTACHMENT (ATTACHED_BY);
CREATE INDEX I_TSK_MNT_TASK ON TASK_ATTACHMENT (TASK_ID);
CREATE INDEX I_TSK_MNT_COMMENTEDBY ON TASK_COMMENT (COMMENTEDBY_ORG_ENTITY_ID);
CREATE INDEX I_TSK_MNT_TASK1 ON TASK_COMMENT (TASK_ID);
